import React, { useState } from "react";
import classes from "./adduser.module.css";
import Card from "../UI/Card";
import Button from "../UI/Button";
const AddUser = () => {
  const intialValue = {
    name: "",
    age: "",
  };
  const [users, setUsers] = useState([]);
  const [userData, setUserData] = useState(intialValue);
  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData((prevData) => ({ ...prevData, [name]: value }));
  };
  const displayData = (event) => {
    const userName = event.target.name.value;
    const userAge = event.target.age.value;
    if (userName === "" || userAge === "") {
      return alert("please fill the data");
    }
    if(+userAge < 1){
      return alert("age is not negative")
    }
    console.log(userName, userAge);
    event.preventDefault();
    setUsers((prevValue) => [...prevValue, userData]);
    setUserData(intialValue);
  };

  return (
    <Card className={classes.input}>
      <form onSubmit={displayData}>
        <label htmlFor="name">UserName</label>
        <input
          type="text"
          id="name"
          name="name"
          value={userData.name}
          onChange={handleChange}
        />
        <label htmlFor="age"> Age (Years)</label>
        <input
          type="number"
          id="age"
          name="age"
          value={userData.age}
          onChange={handleChange}
        />
        <Button type="submit">Add User</Button>
      </form>
    </Card>
  );
};

export default AddUser;
